$(document).ready(function(){$('.unim,.unim-squre,.unim-circle,.unim-portrait,.unim-landscape').each(function(index,element){var dynamicDiv='';var source=$(this).attr("src");var height=$(this).attr("height");var divClass=$(this).attr("class");if(parseInt(height)>0){dynamicDiv='<div class="'+divClass+' unim-height" style="height: '+height+'px;">';}else{dynamicDiv='<div class="'+divClass+'">';} dynamicDiv+='<div class="unim-placeholder">';dynamicDiv+='<div>';dynamicDiv+='<div class="unim-icon"></div>';dynamicDiv+='</div>';dynamicDiv+='</div>';dynamicDiv+='<div class="unim-img" style="background-image: url('+source+');"></div>';dynamicDiv+='</div>';$(this).replaceWith(dynamicDiv);});})
